import React, { useEffect, useState } from 'react'
import { Box , Button , Stack,TextField , Typography } from '@mui/material'
import { fetchData , exerciseOptions } from '../utils/fetchData';
import HorizontalScrollbar from './HorizontalScrollbar';



const SearchExercises = ({setExercises,setBodyPart,bodyPart}) => {
  const [search, setSearch] = useState('');

  const [bodyParts, setBodyParts] = useState([]);

  useEffect(() => {
    const fetchExercisesData = async () => {
      const bodyPartsData = await fetchData('https://exercisedb.p.rapidapi.com/exercises/bodyPartList',exerciseOptions);

      setBodyParts(['all',...bodyPartsData]);
    }


    fetchExercisesData();
  }, [])


  const handleSearch = async () => {
    if(search){
      const exerciseData = await fetchData('https://exercisedb.p.rapidapi.com/exercises',exerciseOptions);


      const searchedExercises = exerciseData.filter((exercise) => exercise.name.toLowerCase().includes(search) || 
      exercise.target.toLowerCase().includes(search) ||
      exercise.equipment.toLowerCase().includes(search) ||
      exercise.bodyPart.toLowerCase().includes(search)


      )
      setSearch('');
      setExercises(searchedExercises);
    }
  }

  return (
    <Stack alignItems="center" mt="37px" justifyContent="center" p="20px">
        <Typography fontWeight={700} sx={{ fontSize: { lg: '44px', xs: '30px' } }} mb="49px" textAlign="center">
        Awesome Exercises You <br /> Should Know
      </Typography>
      <Box position='relative' mb='72px' sx={{
        mr:{lg:'10%',xs:'20%'}
      }}>
        <TextField height='76px' value={search} onChange={(e) => setSearch(e.target.value.toLowerCase())}
        placeholder='Search Exercises'
        type='text'
         sx={{
            input:{fontWeight : '700' , border:'none' , borderRadius:'4px'},
            width:{lg:'1000px' ,xs:'400px'},
            ml:{xs:'10%'},
            backgroundColor:'#fff',
            borderRadius:'40px'
         }}/>
         <Button className='search-btn' sx={{
            bgcolor:'#FF2625',
            color:'#fff',
            textTransform:'none',
            width:{lg : '175px' , xs:'80px'},
            fontSize:{lg : '20px' ,xs :'14px'},
            height:'56px',
            position:'absolute'
         } } onClick={handleSearch}>Search</Button>
      </Box>
      <Box sx={{
        position:'relative' ,
        width:'100%',
        padding:'20px'
      }}>
        <HorizontalScrollbar bodyPart={bodyPart} setBodyPart={setBodyPart} data={bodyParts} />
      </Box>
    </Stack>
  )
}

export default SearchExercises
